var Render = function(view, locals){
  locals = locals || window;
  locals["_"] = locals["_"] || _;
  locals["Moment"] = locals["Moment"] || Moment;
  locals["Belt"] = locals["Belt"] || Belt;
  locals["Str"] = locals["Str"] || Str;  locals.Render = locals.Render || Render;

  return Templates[view](locals);
};

