  "                    <% } else { %>",
  "                      <a href=\"/products/<%= encodeURIComponent(k) %>/<%= encodeURIComponent(k2) %>\" data-category=\"<%= k + ' > ' + k2 %>\" class=\"product-filter-list__link\"><%= k2 %></a>",
  "                    <% } %>",
  "                  </li>",
  "                <% }) %>",
  "              </ul>",
  "            </div>",
  "          </div>",
  "        </div>",
  "      <% }) %>",
  "      <div class=\"card product-filter-item hidden-xs-up\">",
  "        <div class=\"card-header product-filter-item__header\" role=\"tab\" id=\"headingFifth\">",
  "          <h5 class=\"mb-0\">",
