, "admin_inventory_rule": ([
  "<div id=\"container\" class=\"row\">",
  "  <div class=\"col-lg-12\">",
  "    <div class=\"ibox float-e-margins\">",
  "      <div class=\"ibox-content\">",
  "        <div class=\"pull-right\">",
  "          <button name=\"submit\" class=\"btn btn-primary\" type=\"submit\"><%= data.button_title %></button>",
  "          <a href=\"/admin/inventory_rule/<%= Request.data()._id %>/read.json\" target=\"_blank\" class=\"btn btn-default\">JSON</a>",
  "        </div>",
  "        <h1><%= title %></h1>",
  "        <div class=\"hr-line-dashed\"></div>",
  "        <p>",
  "          This rule is triggered when: <strong data-set=\"description.1\"></strong>",
  "        </p>",
  "        <p>",
  "          When triggered, product will be: <strong data-set=\"description.0\"></strong>",
  "        </p>",
  "        <div class=\"hr-line-dashed\"></div>",
  "        <form class=\"form-horizontal\">",
  "          <div class=\"form-group\">",
  "            <label class=\"col-sm-1 control-label\">Term</label>",
  "            <div class=\"col-sm-11\">",
  "              <input name=\"code\" type=\"text\" class=\"form-control\" placeholder=\"Term\" data-get=\"term\" data-get-method=\"val()\" data-set=\"term\" data-set-method=\"val\">",
  "            </div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "          <div class=\"form-group\">",
  "            <label class=\"col-sm-1 control-label\">Brand</label>",
  "            <div class=\"col-sm-11\">",
  "              <input name=\"error_label\" type=\"text\" class=\"form-control\" placeholder=\"Brand\" data-get=\"brand\" data-get-method=\"val()\" data-set=\"brand\" data-set-method=\"val\">",
  "            </div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "          <div class=\"form-group\">",
  "            <label class=\"col-sm-1 control-label\">Vendor</label>",
  "            <div id=\"vendor_dropdown\" class=\"col-sm-11\">",
  "              <select name=\"vendor\" type=\"text\" class=\"form-control\">",
  "                <option value=\"\">< No vendor ></option>",
  "                <% _.each(_.sortBy(Instance.vendor_ids, function(v){",
  "                  return v.name;",
  "                }), function(v, k){ %>",
  "                  <option value=\"<%= v._id %>\"><%= v.name %></option>",
  "                <% }) %>",
  "              </select>",
  "              <input type=\"hidden\" class=\"form-control\" placeholder=\"Vendor\" data-get=\"vendor\" data-get-method=\"val()\" data-set=\"vendor\" data-set-method=\"val\">",
  "            </div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "          <div class=\"form-group\">",
  "            <div class=\"col-sm-12\">",
  "              <div>",
  "                <label><input type=\"checkbox\" class=\"i-checks\" data-get=\"active\" data-get-method=\"isChecked()\" data-set=\"active\" data-set-method=\"makeChecked\">&nbsp;&nbsp;Is Active?</label>",
  "              </div>",
  "            </div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "          <div class=\"form-group\">",
  "            <label class=\"col-sm-1 control-label\">Categorize As</label>",
  "            <div id=\"product_category_dropdown\" class=\"col-sm-11\">",
  "              <select type=\"text\" class=\"form-control\" name=\"product_category\"></select>",
  "              <input type=\"hidden\" class=\"form-control\" placeholder=\"Product Category\" data-get=\"product_category\" data-get-method=\"val()\" data-set=\"product_category\" data-set-method=\"val\">",
  "              <% if (data.button_title === 'Update'){ %>",
  "                <small class=\"form-text text-muted\">Current Category: <strong data-set=\"product_category\"></strong></small>",
  "              <% } %>",
  "            </div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "          <div class=\"form-group\">",
  "            <div class=\"col-sm-12\">",
  "              <div>",
  "                <label><input type=\"checkbox\" class=\"i-checks\" data-get=\"product_hide\" data-get-method=\"isChecked()\" data-set=\"product_hide\" data-set-method=\"makeChecked\">&nbsp;&nbsp;Hide Product</label>",
  "              </div>",
  "            </div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "          <div class=\"form-group\">",
  "            <div class=\"col-sm-12\">",
  "              <div>",
  "                <label><input type=\"checkbox\" class=\"i-checks\" data-get=\"product_show\" data-get-method=\"isChecked()\" data-set=\"product_show\" data-set-method=\"makeChecked\">&nbsp;&nbsp;Show Product</label>",
  "              </div>",
  "            </div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "          <div class=\"form-group\">",
  "            <label class=\"col-sm-1 control-label\">Rename Brand</label>",
  "            <div class=\"col-sm-11\">",
  "              <input name=\"error_label\" type=\"text\" class=\"form-control\" placeholder=\"Product Brand\" data-get=\"product_brand\" data-get-method=\"val()\" data-set=\"product_brand\" data-set-method=\"val\">",
  "            </div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "        </form>",
  "        <div class=\"pull-right\">",
  "          <button name=\"submit\" class=\"btn btn-primary\" type=\"submit\"><%= data.button_title %></button>",
  "        </div>",
  "      </div>",
  "    </div>",
  "  </div>",
  "</div>",
  ""
].join("\n"))

