, "admin_inventory_rule_list_row": ([
  "<tr class=\"inventory_rule\" data-id=\"<%= _id %>\">",
  "  <td>",
  "    <% if (typeof term !== 'undefined'){ %>",
  "      <p>TERM: <strong><%= term %></strong><p>",
  "    <% } %>",
  "    <% if (typeof brand !== 'undefined'){ %>",
  "      <p>BRAND: <strong><%= brand %></strong><p>",
  "    <% } %>",
  "    <% if (typeof vendor !== 'undefined'){ %>",
  "      <p>VENDOR: <strong><%= Instance.VendorName(vendor) %> (<%= vendor %>)</strong><p>",
  "    <% } %>",
  "    <% if (typeof active !== 'undefined' && active){ %>",
  "      <p><span class=\"label label-success\">ACTIVE</span><p>",
  "    <% } %>",
  "    <% if (typeof active !== 'undefined' && !active){ %>",
  "      <p><span class=\"label\">INACTIVE</span><p>",
  "    <% } %>",
  "    <hr>",
  "    <p>",
  "      This rule is triggered when: <strong><%= description[1] %></strong>",
  "    </p>",
  "    <p>",
  "      When triggered, product will be: <strong><%= description[0] %></strong>",
  "    </p>",
  "  </td>",
  "  <td>",
  "    <% if (typeof product_category !== 'undefined'){ %>",
  "      <p><%= product_category %><p>",
  "    <% } %>",
  "  </td>",
  "  <td>",
  "    <% if (typeof product_hide !== 'undefined' && product_hide){ %>",
  "      <p><i class=\"fa fa-check-circle\"></i><p>",
  "    <% } %>",
  "  </td>",
  "  <td>",
  "    <% if (typeof product_show !== 'undefined' && product_show){ %>",
  "      <p><i class=\"fa fa-check-circle\"></i><p>",
  "    <% } %>",
  "  </td>",
  "  <td>",
  "    <% if (typeof product_brand !== 'undefined'){ %>",
  "      <p><%= product_brand %><p>",
  "    <% } %>",
  "  </td>",
  "  <td data-set=\"actions\" data-set-method=\"html\">",
  "    <a href=\"/admin/inventory_rule/<%= _id %>/read\" class=\"btn btn-default\">Edit</a>",
  "    <a href=\"/admin/product/list?query=<%= encodeURIComponent(query) %>\" target=\"_blank\" class=\"btn btn-default\">Products</a>",
  "    <a href=\"/admin/inventory_rule/<%= _id %>/process.json\" target=\"_blank\" class=\"btn btn-info\">Process Rule</a>",
  "    <button name=\"delete\" class=\"btn btn-danger\">Delete</button>",
  "  </td>",
  "</tr>",
  ""
].join("\n"))

