  "          </div>",
  "          <div class=\"form-group\">",
  "            <label class=\"col-sm-4 control-label\">Last Name</label>",
  "            <div class=\"col-sm-8\"><input name=\"last_name\" type=\"text\" class=\"form-control\"></div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
  "          <div class=\"form-group\">",
  "            <label class=\"col-sm-4 control-label\">Email</label>",
  "            <div class=\"col-sm-8\"><input name=\"email\" type=\"text\" class=\"form-control\"></div>",
  "          </div>",
  "          <div class=\"hr-line-dashed\"></div>",
